mapper.update:
UPDATE
	tb_goods
SET
	goodsName= #{goodsName}
	,categoryCode = #{categoryCode}
	,businessCode = #{businessCode}
	,taxType = #{taxType}
	,weight = #{weight}
	,unit = #{unit}
	,maker = #{maker}
WHERE
	barcode=#{barcode}

mapper.select:
SELECT g.barcode, g.businessCode, g.categoryCode, g.goodsName, g.weight, g.unit , g.maker, g.taxType, g.purchasePrice, g.salesPrice, g.margin, g.uncheckedQTY , g.warehouseStock, g.showcaseStock, g.goodsTotalStock, g.martCode, g.ID,g.regDate , c.categoryName, b.businessName, u.name, g.deleteStatus FROM tb_category AS c INNER JOIN tb_goods AS g on c.categoryCode = g.categoryCode INNER join tb_business AS b on g.businessCode = b.businessCode INNER join tb_user AS u ON g.ID = u.ID
AND g.categoryCode = #{searchCategory}
AND g.purchasePrice >= #{minPurchasePrice}
AND #{maxPurchasePrice} >= g.purchasePrice
AND g.taxType = #{searchTaxType}
AND g.salesPrice >= #{minSalesPrice}
AND #{maxSalesPrice} >= g.salesPrice
AND g.goodsName LIKE CONCAT('%', #{searchGoodsName}, '%')
AND g.martCode = #{martCode}
AND g.deleteStatus = 'N'

mapper.update:
UPDATE 
	tb_goods
SET
	salesPrice= #{salesPrice}
WHERE
	barcode= #{barcode}

mapper.insert:
INSERT INTO tb_salesPrice(
	barcode
	, salesPrice
	, martCode
	, ID
	, regDate
)VALUES(
	#{barcode}
	,#{salesPrice}
	,#{martCode}
	,#{ID}
	,NOW()
);

mapper.update:
UPDATE
	tb_category
SET
	categoryName = #{categoryName}
	,regDate = NOW()
	,ID = #{id}
WHERE
	categoryCode=#{categoryCode}

mapper.insert:
INSERT INTO tb_category(
	categoryName, martCode, ID, regDate
)VALUES (
	#{categoryName}
	,#{martCode}
	,#{id}
	,NOW()
)
