mapper.select:
SELECT o.orderCode, o.businessCode, o.orderNum, o.deliveryDate, o.state, o.purchaseDate, o.martCode, o.id, o.regDate, b.businessName, date_format(o.regDate, '%Y-%m-%d') AS regDate FROM tb_order AS o INNER JOIN tb_business AS b ON o.businessCode = b.businessCode
AND ${searchKey} LIKE CONCAT('%', #{searchValue}, '%');
AND date_format(o.regDate, '%Y-%m-%d') BETWEEN #{startDt} AND #{endDt}
