mapper.select:
SELECT u.ID, u.PW, u.name, u.birthday, u.email, u.martCode, u.levelNum, u.regDate ,l.levelName, m.martName, u.deleteStatus FROM tb_user AS u INNER JOIN tb_systemLevel AS l ON u.levelNum = l.levelNum INNER JOIN tb_mart AS m ON u.martCode = m.martCode WHERE u.martCode != 'GOODSJOC'
AND u.deleteStatus = #{deleteStatus}
AND u.regDate >= #{startDate}
AND #{endDate} >= u.regDate
AND u.levelNum = #{levelNum}
AND m.martName LIKE CONCAT('%', #{martName}, '%')
AND (u.name LIKE CONCAT('%', #{name}, '%') OR u.ID LIKE CONCAT('%', #{name}, '%'))
ORDER BY u.martCode ASC, u.levelNum ASC
